import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'eventify-demo';
}
<header>
  <h1 class="text-heading"><a routerLink="/">EVENTIFY <i class="fa-solid fa-calendar-days"></i></a></h1>
  <nav>
    <ul>
      <li><a routerLink="/home">Homepage</a></li>
      <li><a routerLink="/login">Login</a></li>
      <li><a routerLink="/signup">Signup</a></li>
      <li><a routerLink="/searchevents">Search</a></li>
      <li><a routerLink="/createupdateevent">Create/Update Event</a></li>
    </ul>
  </nav>
</header>

<router-outlet></router-outlet>
