      enddate: new Date("2023-02-27 17:04:27"),
      eventduration: 4,
      eventCapacity: 96,
      major_type_id: 5,
      event_type:"Games"
    } as Event;


   }



  ngOnInit(): void {
    this.loginService.getRecommendedEvents(this.event).subscribe((response) => {
      debugger;
